// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";

contract MedRecChain is AccessControl {
    //ROLES
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant HOSPITAL_ROLE = keccak256("HOSPITAL_ROLE");
    bytes32 public constant DOCTOR_ROLE = keccak256("DOCTOR_ROLE");
    bytes32 public constant PATIENT_ROLE = keccak256("PATIENT_ROLE");

    // Admin refers to government, It hard coded by us.
    //address public Admin = 0xe374D964318B4A333dB68C776674C6788092660A;
    address public Admin;
    // event
    event AccessGranted(
        address indexed Doctor_Address,
        address indexed Patient_Address
    ); //by patient
    event AccessRejected(
        address indexed Doctor_Address,
        address indexed Patient_Address
    ); //by patient
    event RequestAccess(address indexed requester, address indexed patient); //by doctor or (patient)

    // [_patient][_doctor] = bool
    mapping(address => mapping(address => bool)) isAuth;

    struct Hospital {
        uint256 id;
        string name;
        address addr;
        string place;
        uint256 phone;
    }

    mapping(address => Hospital) Hospitals;
    address[] public Hospitals_keys;

    struct patient {
        uint256 id;
        string name;
        string email;
        string National_Addr;
        uint256 National_id;
        uint256 age;
        address PatientAddress;
        uint256 phone;
        string Blood_Type;
        string marital_status;
        string gender;
        address hospital_addr;
        record[] Records;
    }

    mapping(address => patient) patients;
    address[] private patients_keys;

    struct doctor {
        uint256 id;
        string name;
        string hospital_name;
        address hospital_addr;
        uint256 phone;
        string email;
        uint256 age;
        string place;
        string Medical_specialty;
        address docAddress;
    }

    mapping(address => doctor) doctors;
    address[] private doctors_keys;

    struct record {
        uint Medical_id;
        string category;
        string patient_name;
        string rec_name;
        string Created_at;
        address patient_addr;
        address doctor_addr;
        string hex_ipfs; // file, image date
        string notes;
    }

    // for Ids
    uint256 Hospital_index;
    uint256 Doctor_index;
    uint256 Patient_index;

    uint256 Record_index;
    uint256 Request_index;

    constructor() {
        Admin=msg.sender;
        _setupRole(ADMIN_ROLE, Admin);
    }

    // function to know who logg in
    function logg() public view returns (uint role) {
        if (hasRole(ADMIN_ROLE, msg.sender)) {
            return 1;
        } else if (hasRole(HOSPITAL_ROLE, msg.sender)) {
            return 2;
        } else if (hasRole(DOCTOR_ROLE, msg.sender)) {
            return 3;
        } else if (hasRole(PATIENT_ROLE, msg.sender)) {
            return 4;
        } else {
            return 0;
        }
    }

    //To check the role of who deploye the contract.
    modifier onlyAdmin() {
        require(
            hasRole(ADMIN_ROLE, msg.sender),
            "Only superAdmin has permission to do that action"
        );
        _;
    }

    modifier onlyHospital() {
        require(
            hasRole(HOSPITAL_ROLE, msg.sender),
            "This account is not Hospital"
        );
        _;
    }
    modifier onlydoctors() {
        require(hasRole(DOCTOR_ROLE, msg.sender), "This account is not Doctor");
        _;
    }
    modifier onlypatient() {
        require(
            hasRole(PATIENT_ROLE, msg.sender),
            "This account is not patient"
        );
        _;
    }

    //////////////////////////////////////

    //Tasks done by Admin (defult exisit)
    //1. add hospital
    //2. delete hospital
    //3. add doctor
    //4. delete doctor
    //5. see all hospital in system
    //6. see all doctors in system

    function addhospital(
        string memory _name,
        address _address,
        string memory _place,
        uint256 _phone
    ) public onlyAdmin returns (bool success) {
        require(
            !hasRole(HOSPITAL_ROLE, _address),
            "This hospital is really exisit!! "
        );
        require(!hasRole(ADMIN_ROLE, _address), "This Account is Admin!! ");
        require(!hasRole(DOCTOR_ROLE, _address), "This Account is Doctor!! ");
        require(!hasRole(PATIENT_ROLE, _address), "This Account is Patient!! ");

        _setupRole(HOSPITAL_ROLE, _address);
        Hospital_index = Hospital_index + 1;
        Hospitals[_address] = Hospital(
            Hospital_index,
            _name,
            _address,
            _place,
            _phone
        );
        Hospitals_keys.push(_address);
        return true;
    }

    function removeitem_hos(address _hosAddress) public {
        require(Hospitals_keys.length > 0, "No keys in array");
        for (uint i = 0; i < Hospitals_keys.length; i++) {
            if (
                keccak256(abi.encodePacked(Hospitals_keys[i])) ==
                keccak256(abi.encodePacked(_hosAddress))
            ) {
                delete Hospitals_keys[i];
                uint lastIndex = Hospitals_keys.length - 1;
                address lastId = Hospitals_keys[lastIndex];
                Hospitals_keys[i] = lastId;
                Hospitals_keys.pop();
                break;
            }
        }
    }

    function removehospital(
        address _address
    ) public onlyAdmin returns (bool success) {
        require(
            hasRole(HOSPITAL_ROLE, _address),
            "This hospital is not exisit"
        );
        _revokeRole(HOSPITAL_ROLE, _address);
        delete Hospitals[_address];
        removeitem_hos(_address);
        return true;
    }

    function get_all_hospitals() public view returns (Hospital[] memory) {
        Hospital[] memory hos = new Hospital[](Hospitals_keys.length);
        for (uint256 i = 0; i < Hospitals_keys.length; i++) {
            hos[i] = Hospitals[Hospitals_keys[i]];
        }
        return hos;
    }

    ///////////////

    function addDoctor(
        string memory _name,
        string memory _hospital_name,
        address _hospital_addr,
        uint256 _phone,
        string memory _email,
        uint256 _age,
        string memory _place,
        string memory _Medical_specialty,
        address _docAddress
    ) public onlyAdmin returns (bool success) {
        require(
            !hasRole(DOCTOR_ROLE, _docAddress),
            "This account already a doctor"
        );
        require(!hasRole(ADMIN_ROLE, _docAddress), "This Account is Admin!! ");
        require(
            !hasRole(HOSPITAL_ROLE, _docAddress),
            "This Account is Hospital!! "
        );
        require(
            !hasRole(PATIENT_ROLE, _docAddress),
            "This Account is Patient!! "
        );
        require(
            hasRole(HOSPITAL_ROLE, _hospital_addr),
            "This hospital is really exisit!! "
        );
        _setupRole(DOCTOR_ROLE, _docAddress);
        Doctor_index = Doctor_index + 1;
        doctors[_docAddress] = doctor(
            Doctor_index,
            _name,
            _hospital_name,
            _hospital_addr,
            _phone,
            _email,
            _age,
            _place,
            _Medical_specialty,
            _docAddress
        );
        doctors_keys.push(_docAddress);
        return true;
    }

    function removeitem_doc(address _docAddress) public {
        require(doctors_keys.length > 0, "No keys in array");
        for (uint i = 0; i < doctors_keys.length; i++) {
            if (
                keccak256(abi.encodePacked(doctors_keys[i])) ==
                keccak256(abi.encodePacked(_docAddress))
            ) {
                delete doctors_keys[i];
                uint lastIndex = doctors_keys.length - 1;
                address lastId = doctors_keys[lastIndex];
                doctors_keys[i] = lastId;
                doctors_keys.pop();
                break;
            }
        }
    }

    function removeDoctor(
