            "The person you are requesting access to is not a Patient"
        );
        require(
            !checkRequestExists(_doctor, _patient),
            "already send a request"
        );
        Request_index = Request_index + 1;
        patient memory pati = get_patient_by_address(_patient);
        doctor memory doc = get_doctor_by_address(_doctor);
        requests.push(
            request(
                Request_index,
                _doctor,
                _patient,
                block.timestamp,
                pati.name,
                doc.name
            )
        );
        emit RequestAccess(msg.sender, _patient);

        return true;
    }

